mapper.select:

select user_name
from user_table
where user_id = #{user_id}


select user_name
from user_table
where user_tel = #{user_tel}


select user_idx, user_name
from user_table
where user_id=#{user_id} and user_pw=#{user_pw}


select user_id, user_name, user_tel, user_address, user_info
from user_table
where user_idx = #{user_idx}

mapper.update:

update user_table
set user_pw = #{user_pw}, user_tel = #{user_tel}, user_address = #{user_address} 
where user_idx = #{user_idx}

mapper.insert:
insert into user_table(
	user_idx
	, user_id
	, user_name
	, user_pw
	, user_tel
	, user_info
	, therapist_license
	, therapist_picture
	, therapist_career
	, therapist_level
	, therapist_resume				
	, company_id
) values (
	user_seq.nextval
	, #{user_id}
	, #{user_name}
	, #{user_pw}
	, #{user_tel}
	, #{user_info}
	, #{therapist_license, jdbcType=VARCHAR}
	, #{therapist_picture, jdbcType=VARCHAR}
	, #{therapist_career,jdbcType=VARCHAR}
	, #{therapist_level, jdbcType=VARCHAR}
	, #{therapist_resume, jdbcType=VARCHAR}
	, #{company_id}
)

mapper.select:

select *
from user_table natural join COMPANY_TABLE
where user_idx=#{userIdx}
